import React, { useEffect, useState } from "react";
import { FiArrowRight } from "react-icons/fi";
import { useParams } from "react-router-dom";
import { toast } from "react-toastify";
import { create_order, getAllProductsApi } from "../apis/Api";


const HomePage = () => {
  const [products, setProducts] = useState([]);
  const [searchQueryUsers, setSearchQueryUsers] = useState("");
  const { id } = useParams();
  const [cart, setCart] = useState([]);
  const [cartValue, setCartValue] = useState(1);
  const [userData, setUserData] = useState(null);
  


  useEffect(() => {
    getAllProductsApi(id).then((res) => {
      setProducts(res.data.products);
    });
  }, []);
  
  const handleSearchUsers = (e) => {
    e.preventDefault();
    
    const filteredUsers = products.filter((product) => {
      const lowerCaseQuery = searchQueryUsers.toLowerCase();
      return product.productName.toLowerCase().includes(lowerCaseQuery);
    });
    setProducts(filteredUsers);
  };

  const addToCart = (index) => {
    const storedUserData = JSON.parse(localStorage.getItem("user"));
    setUserData(storedUserData);
  
    const productToAdd = products[index]; 
  
    if (!cart.find((item) => item.id === productToAdd._id)) {
      const orderData = {
        userId: storedUserData._id,
        productId: productToAdd._id,
        orderId: index.toString(),
        quantity: cartValue,
      };
  
      create_order(orderData)
        .then((res) => {
          if (res.data.success === false) {
            toast.error(res.data.message);
          } else {
            setCart([
              ...cart,
              {
                id: productToAdd._id,
                name: productToAdd.productName,
                price: productToAdd.productPrice,
                quantity: cartValue,
                orderId: res.data.order.orderId,
              },
            ]);
            toast.success("Item added to cart!");
          }
        })
        .catch((err) => {
          toast.error("Server Error");
          console.log(err.message);
        });
    } else {
      toast.warning("Item is already in the cart!");
    }
  };
  

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '30px' }}>
      <h1 style={{ textAlign: 'center', color: '#FF6347', fontWeight: 'bold' }}>Find your bakery product</h1>

      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', marginTop: '20px', position: 'relative' }}>
        <img
          src="https://cdn.pixabay.com/photo/2017/05/23/22/33/birthday-2338813_1280.jpg"
          alt="Introduction"
          style={{ height: '500px', width: '100%', borderRadius: '10px' }}
        />
        <button
          className="secondary-button"
          style={{
            position: 'absolute',
            top: '70%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            padding: '15px 30px',
            background: '#FF4500',
            color: '#fff',
            borderRadius: '25px',
            cursor: 'pointer',
            border: 'none',
            fontSize: '18px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)'
          }}
        >
          Get Started <FiArrowRight />
        </button>
      </div>

      <form className="d-flex me-2" onSubmit={handleSearchUsers} style={{ justifyContent: 'center', marginTop: '20px' }}>
        <input
          className="form-control me-2"
          type="text"
          placeholder="Search Product..."
          aria-label="Search"
          value={searchQueryUsers}
          onChange={(e) => setSearchQueryUsers(e.target.value)}
          style={{
            width: '300px',
            padding: '10px',
            marginRight: '10px',
            borderRadius: '25px',
            border: '1px solid #ccc',
            marginTop: '10px'
          }}
        />
        <button className="btn btn-outline-success" type="submit" style={{
          padding: '10px 20px',
          borderRadius: '25px',
          border: '1px solid #28a745',
          marginTop: '10px',
          backgroundColor: '#28a745',
          color: '#fff',
          cursor: 'pointer'
        }}>Search</button>
      </form>

      <h2 style={{ textAlign: 'center', color: '#FF4500', fontWeight: 'bold', marginTop: '20px' }}>New Arrivals</h2>

      <div style={{ display: 'flex', justifyContent: 'space-around', flexWrap: 'wrap', marginTop: '20px' }}>
        {products.map((product, index) => (
          <div key={product._id} style={{
            margin: '20px',
            padding: '15px',
            border: '1px solid #ddd',
            borderRadius: '10px',
            textAlign: 'center',
            width: '250px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
            backgroundColor: '#fff'
          }}>
            <img src={product.productImageUrl} alt={product.productName} style={{
              width: '100%',
              height: '200px',
              objectFit: 'cover',
              borderRadius: '5px'
            }} />
            <h3 style={{ marginTop: '10px', color: '#333' }}>{product.productName}</h3>
            <h3 style={{ marginTop: '10px', color: '#333' }}>${product.productPrice}</h3>
            <button onClick={() => addToCart(index)} style={{
              padding: '10px',
              background: '#FF6347',
              color: '#fff',
              borderRadius: '5px',
              cursor: 'pointer',
              border: 'none',
              marginTop: '10px'
            }}>
              Add to Cart
            </button>
          </div>
        ))}
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', marginTop: '40px', gap: '20px' }}>
        <div style={{
          textAlign: 'left',
          padding: '20px',
          backgroundColor: '#FF6347',
          borderRadius: '10px',
          color: '#fff'
        }}>
          <h3 style={{ fontWeight: 'bold' }}>Our Services</h3>
          <ul>
            <li>Buy Bakery product</li>
            <li>Bakery</li>
            <li>Bakeryshop</li>
          </ul>
        </div>
        <div style={{
          textAlign: 'left',
          padding: '20px',
          backgroundColor: '#FF6347',
          borderRadius: '10px',
          color: '#fff'
        }}>
          <h3 style={{ fontWeight: 'bold' }}>Location</h3>
          <ul>
            <li>Visit Office</li>
            <li>Shankmul Marg, New Baneshwor</li>
            <li>Chabil Marga, Maharajgunj</li>
          </ul>
        </div>
        <div style={{
          textAlign: 'left',
          padding: '20px',
          backgroundColor: '#FF6347',
          borderRadius: '10px',
          color: '#fff'
        }}>
          <h3 style={{ fontWeight: 'bold' }}>Contacts</h3>
          <ul>
            <li>Call any time</li>
            <li>9814846609</li>
            <li>Send Email</li>
            <li>getaashish@gmail.com</li>
          </ul>
        </div>
        <div style={{
          textAlign: 'left',
          padding: '20px',
          backgroundColor: '#FF6347',
          borderRadius: '10px',
          color: '#fff'
        }}>
          <h3 style={{ fontWeight: 'bold' }}>About Us</h3>
          <ul>
            <li>Who We Are</li>
            <li>Our Story</li>
            <li>Working at Bakery</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
