import React, { useEffect, useState } from "react";
import { FiArrowRight } from "react-icons/fi";
import { useParams } from "react-router-dom";
import { toast } from "react-toastify";
import { create_order, getAllProductsApi } from "../apis/Api";
import Cart from "./Cart"; // Import the Cart component

const HomePage = () => {
  const [products, setProducts] = useState([]);
  const [searchQueryUsers, setSearchQueryUsers] = useState("");
  const { id } = useParams();
  const [cart, setCart] = useState([]);
  const [cartValue, setCartValue] = useState(1);
  const [userData, setUserData] = useState(null);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    getAllProductsApi(id).then((res) => {
      setProducts(res.data.products);
    });
  }, [id]);

  // search button function
  const handleSearchUsers = (e) => {
    e.preventDefault();
    const filteredUsers = products.filter((products) => {
      const lowerCaseQuery = searchQueryUsers.toLowerCase();
      return products.productName.toLowerCase().includes(lowerCaseQuery);
    });
    setProducts(filteredUsers);
  };

  // add to the cart
  const addToCart = (index) => {
    const storedUserData = JSON.parse(localStorage.getItem("user"));
    setUserData(storedUserData);

    const productToAdd = products[index]; // Get the product based on the index

    if (!cart.find((item) => item.id === productToAdd._id)) {
      const orderData = {
        userId: storedUserData._id,
        productId: productToAdd._id, // Set productId based on productToAdd
        orderId: index.toString(), // Set orderId based on index
        quantity: cartValue,
      };

      create_order(orderData)
        .then((res) => {
          if (res.data.success === false) {
            toast.error(res.data.message);
          } else {
            setCart([
              ...cart,
              {
                id: productToAdd._id,
                name: productToAdd.productName,
                price: productToAdd.productPrice,
                quantity: cartValue,
                orderId: res.data.order.orderId,
              },
            ]);
            alert("Item added to cart!");
          }
        })
        .catch((err) => {
          toast.error("Server Error");
          console.log(err.message);
        });
    } else {
      alert("Item is already in the cart!");
    }
  };

  // add to favorite
  const addToFavorite = (index) => {
    const productToAdd = products[index];
    if (!favorites.find((item) => item.id === productToAdd._id)) {
      setFavorites([...favorites, productToAdd]);
      toast.success("Item added to favorites!");
    } else {
      toast.info("Item is already in favorites!");
    }
  };

  return (
    <div style={{ fontFamily: "Arial, sans-serif", padding: "30px" }}>
      <h1 style={{ textAlign: "center", color: "#FFA500", fontWeight: "bold" }}>
        Find Your Bakery Product
      </h1>

      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          marginTop: "20px",
          position: "relative",
        }}
      >
        <img
          src="https://cdn.pixabay.com/photo/2017/05/23/22/33/birthday-2338813_1280.jpg"
          alt="Introduction"
          style={{ height: "500px", width: "100%", borderRadius: "10px" }}
        />
        <button
          className="secondary-button"
          style={{
            position: "absolute",
            top: "70%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            padding: "20px",
            background: "#FF6347", // Tomato color
            color: "#fff",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Get Started <FiArrowRight />
        </button>
      </div>
      <form className="d-flex me-2" onSubmit={handleSearchUsers} style={{ marginTop: "20px" }}>
        <input
          className="form-control me-2"
          type="text"
          placeholder="Search Product..."
          aria-label="Search"
          value={searchQueryUsers}
          onChange={(e) => setSearchQueryUsers(e.target.value)}
          style={{
            width: "300px",
            padding: "10px",
            marginRight: "10px",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        />
        <button
          className="btn btn-outline-success"
          type="submit"
          style={{
            padding: "10px",
            borderRadius: "5px",
            border: "1px solid #ccc",
            backgroundColor: "#FFA500",
            color: "#fff",
          }}
        >
          Search
        </button>
      </form>

      <h2
        style={{
          textAlign: "center",
          color: "#FF6347",
          fontWeight: "bold",
          marginTop: "20px",
        }}
      >
        New Arrivals
      </h2>

      <div
        style={{
          display: "flex",
          justifyContent: "space-around",
          flexWrap: "wrap",
          marginTop: "20px",
        }}
      >
        {products.map((product, index) => (
          <div
            key={product._id}
            style={{
              margin: "20px",
              padding: "15px",
              border: "1px solid #ddd",
              borderRadius: "10px",
              textAlign: "center",
              backgroundColor: "#f8f8f8",
            }}
          >
            <img
              src={product.productImageUrl}
              alt={product.productName}
              style={{
                width: "200px", // Adjusted width
                height: "200px", // Adjusted height
                objectFit: "cover",
                borderRadius: "5px",
              }}
            />
            <h3 style={{ marginTop: "10px", color: "#333" }}>{product.productName}</h3>
            <p style={{ color: "#FF6347", fontWeight: "bold" }}>${product.productPrice}</p>
            <button
              onClick={() => addToCart(index)}
              style={{
                padding: "10px",
                background: "#32CD32", // LimeGreen color
                color: "#fff",
                borderRadius: "5px",
                cursor: "pointer",
                marginTop: "10px",
              }}
            >
              Add to Cart
            </button>
            <button
              onClick={() => addToFavorite(index)}
              style={{
                padding: "10px",
                background: "#FFA500", // Orange color
                color: "#fff",
                borderRadius: "5px",
                cursor: "pointer",
                marginTop: "10px",
                marginLeft: "10px",
              }}
            >
              Add to Favorite
            </button>
          </div>
        ))}
      </div>

      {/* Grid container for services, location, contacts, and about */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(4, 1fr)",
          marginTop: "40px",
          gap: "20px",
        }}
      >
        <div
          style={{
            padding: "20px",
            backgroundColor: "#FFA500",
            borderRadius: "10px",
            color: "#fff",
          }}
        >
          <h3 style={{ fontWeight: "bold" }}>Our Services</h3>
          <ul>
            <li>Buy Bakery Product</li>
            <li>Bakery</li>
            <li>Bakery Shop</li>
          </ul>
        </div>
        <div
          style={{
            padding: "20px",
            backgroundColor: "#FFA500",
            borderRadius: "10px",
            color: "#fff",
          }}
        >
          <h3 style={{ fontWeight: "bold" }}>Location</h3>
          <ul>
            <li>Visit Office</li>
            <li>Shankamul Marg, New Baneshwor</li>
            <li>Chabil Marga, Maharajgunj</li>
          </ul>
        </div>
        <div
          style={{
            padding: "20px",
            backgroundColor: "#FFA500",
            borderRadius: "10px",
            color: "#fff",
          }}
        >
          <h3 style={{ fontWeight: "bold" }}>Contacts</h3>
          <ul>
            <li>Call Anytime</li>
            <li>9814846609</li>
            <li>Send Email</li>
            <li>getaashish@gmail.com</li>
          </ul>
        </div>
        <div
          style={{
            padding: "20px",
            backgroundColor: "#FFA500",
            borderRadius: "10px",
            color: "#fff",
          }}
        >
          <h3 style={{ fontWeight: "bold" }}>About Us</h3>
          <ul>
            <li>Who We Are</li>
            <li>Our Story</li>
            <li>Working at Bakery</li>
          </ul>
        </div>
      </div>

      {/* Include the Cart component and pass the cart state */}
      <Cart cart={cart} />
    </div>
  );
};

export default HomePage;
