import React, { useState } from 'react';
import { registerApi } from '../apis/Api';
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';

const Register = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [passwordStrength, setPasswordStrength] = useState('');
  const navigate = useNavigate();

  const changeFirstName = (e) => setFirstName(e.target.value);
  const changeLastName = (e) => setLastName(e.target.value);
  const changeEmail = (e) => setEmail(e.target.value);
  const changePassword = (e) => {
    const pwd = e.target.value;
    setPassword(pwd);
    setPasswordStrength(calculatePasswordStrength(pwd));
  };

  const validatePassword = (password) => {
    const minLength = 8;
    const maxLength = 12;
    const hasUppercase = /[A-Z]/.test(password);
    const hasLowercase = /[a-z]/.test(password);
    const hasNumber = /[0-9]/.test(password);
    const hasSpecialChar = /[!@#$%^&*(),.?":{}|<>]/.test(password);

    if (password.length < minLength || password.length > maxLength) {
      return `Password must be between ${minLength} and ${maxLength} characters long.`;
    }

    if (!hasUppercase) {
      return 'Password must contain at least one uppercase letter.';
    }

    if (!hasLowercase) {
      return 'Password must contain at least one lowercase letter.';
    }

    if (!hasNumber) {
      return 'Password must contain at least one number.';
    }

    if (!hasSpecialChar) {
      return 'Password must contain at least one special character.';
    }

    return null;
  };

  const calculatePasswordStrength = (password) => {
    const hasUppercase = /[A-Z]/.test(password);
    const hasLowercase = /[a-z]/.test(password);
    const hasNumber = /[0-9]/.test(password);
    const hasSpecialChar = /[!@#$%^&*(),.?":{}|<>]/.test(password);

    const lengthCriteria = password.length >= 8 && password.length <= 12;
    const premiumCriteria = password.length > 12;
    const strengthCriteria = [hasUppercase, hasLowercase, hasNumber, hasSpecialChar];

    const strength = strengthCriteria.reduce((acc, curr) => acc + (curr ? 1 : 0), 0);

    if (premiumCriteria && strength >= 4) {
      return 'Premium';
    } else if (lengthCriteria && strength >= 4) {
      return 'Strong';
    } else if (lengthCriteria && strength >= 2) {
      return 'Medium';
    } else {
      return 'Weak';
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const error = validatePassword(password);
    if (error) {
      toast.error(error);
      return;
    }

    const data = {
      firstName,
      lastName,
      email,
      password,
    };

    registerApi(data)
      .then((res) => {
        if (res.data.success === true) {
          toast.success(res.data.message);
          navigate('/login'); // Redirect to login after successful registration
        } else {
          toast.error(res.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
        toast.error('Internal Server Error!');
      });
  };

  return (
    <div className="box">
      <body
        style={{
          fontFamily: 'Arial, sans-serif',
          margin: 0,
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: '100vh',
          backgroundImage: "url('https://img.freepik.com/free-photo/ai-generated-cake-picture_23-2150649462.jpg?t=st=1721059061~exp=1721062661~hmac=51ac7fc832c38a1db6d18b0486aa0dcf75fd3dcad9228003a5b193746c0f2a11&w=900')",
          backgroundSize: 'cover',
          backgroundPosition: 'center',
        }}
      >
        <div className="register-container" style={{ border: '10px solid #d39e6a', borderRadius: '20px', padding: '20px', borderBlockColor:'#8B4513'}}>
          <h1 style={{textAlign: 'left', color: '#8B4513', marginTop: '0', marginBottom: '20px', fontSize: '3em'}}>Create Your Account!</h1>
          <form className="w-100" onSubmit={handleSubmit}>
            <div className="form-group m-2 fw-bold">
              <label style={{ color: 'orange', textAlign:'center', }}>Firstname</label>
              <input 
                onChange={changeFirstName} 
                className="form-control" 
                type="text" 
                placeholder="Enter your Firstname" 
                required 
                style={{ padding: '10px', borderRadius: '5px', border: '2px solid #ddd', borderColor:'#8B4513' }}
              />
            </div>
            <div className="form-group m-2 fw-bold">
              <label style={{ color: 'orange', textAlign:'center' }}>Lastname</label>
              <input 
                onChange={changeLastName} 
                className="form-control" 
                type="text" 
                placeholder="Enter your Lastname" 
                required 
                style={{ padding: '10px', borderRadius: '5px', border: '2px solid #ddd', borderColor:'#8B4513' }}
              />
            </div>
            <div className="form-group m-2 fw-bold">
              <label style={{ color: 'orange', textAlign:'center' }}>Email</label>
              <input 
                onChange={changeEmail} 
                className="form-control" 
                type="email" 
                placeholder="Enter your email" 
                required 
                style={{ padding: '10px', borderRadius: '5px', border: '2px solid #ddd', borderColor:'#8B4513' }}
              />
            </div>
            <div className="form-group m-2 fw-bold">
              <label style={{ color: 'orange', textAlign:'center' }}>Password</label>
              <input 
                onChange={changePassword} 
                className="form-control" 
                type="password" 
                placeholder="Enter your password" 
                required 
                style={{ padding: '10px', borderRadius: '5px', border: '2px solid #ddd', borderColor:'#8B4513' }}
              />
              <p style={{ textAlign: 'left', color: '#333', marginTop: '10px', fontWeight: 'bold' }}>
                Password Strength: <span style={{ color: passwordStrength === 'Premium' ? 'gold' : passwordStrength === 'Strong' ? 'green' : passwordStrength === 'Medium' ? 'orange' : 'red' }}>
                  {passwordStrength}
                </span>
                {passwordStrength === 'Premium' && (
                  <span style={{ marginLeft: '10px', color: 'gold', fontWeight: 'bold' }}>
                    ★ Premium
                  </span>
                )}
              </p>
            </div>
            <button className="btn btn-warning m-2 w-25" type="submit" style={{ width: '20%', borderRadius: '10px', textAlign: 'center'}}>
              Submit
            </button>
            <p className="mt-3" style={{ textAlign: 'center', color: '#333' }}>
              Already have an account? <a href="/login" className="text-dark text-decoration-dark fw-bold">Login here</a>
            </p>
          </form>
        </div>
      </body>
    </div>
  );
};

export default Register;
