import React, { useEffect, useState } from "react";
import { FiArrowRight } from "react-icons/fi";
import { useParams } from "react-router-dom";
import { toast } from "react-toastify";
import { create_order, getAllProductsApi } from "../apis/Api";

const HomePage = () => {
  const [products, setProducts] = useState([]);
  const [searchQueryUsers, setSearchQueryUsers] = useState("");
  const { id } = useParams();
  const [cart, setCart] = useState([]);
  const [cartValue, setCartValue] = useState(1);
  const [userData, setUserData] = useState(null);
  const [favorites, setFavorites] = useState([]);

  useEffect(() => {
    getAllProductsApi(id).then((res) => {
      setProducts(res.data.products);
    });
  }, []);

  const handleSearchUsers = (e) => {
    e.preventDefault();
    const filteredUsers = products.filter((product) => {
      const lowerCaseQuery = searchQueryUsers.toLowerCase();
      return product.productName.toLowerCase().includes(lowerCaseQuery);
    });
    setProducts(filteredUsers);
  };

  const addToCart = (index) => {
    const storedUserData = JSON.parse(localStorage.getItem("user"));
    setUserData(storedUserData);
    const productToAdd = products[index];
    if (!cart.find((item) => item.id === productToAdd._id)) {
      const orderData = {
        userId: storedUserData._id,
        productId: productToAdd._id,
        orderId: index.toString(),
        quantity: cartValue,
      };
      create_order(orderData)
        .then((res) => {
          if (res.data.success === false) {
            toast.error(res.data.message);
          } else {
            setCart([
              ...cart,
              {
                id: productToAdd._id,
                name: productToAdd.productName,
                price: productToAdd.productPrice,
                quantity: cartValue,
                orderId: res.data.order.orderId,
              },
            ]);
            toast.success("Item added to cart!");
          }
        })
        .catch((err) => {
          toast.error("Server Error");
          console.log(err.message);
        });
    } else {
      toast.warning("Item is already in the cart!");
    }
  };

  const addToFavorites = (index) => {
    const productToAdd = products[index];
    if (!favorites.find((item) => item.id === productToAdd._id)) {
      setFavorites([...favorites, productToAdd]);
      toast.success("Item added to favorites!");
    } else {
      toast.warning("Item is already in favorites!");
    }
  };

  return (
    <div style={{ fontFamily: 'Arial, sans-serif', padding: '30px' }}>
      <h1 style={{ textAlign: 'center', color: '#FF69B4', fontWeight: 'bold' }}>Find your bakery product</h1>

      <div style={{ position: 'relative', marginBottom: '40px' }}>
        <img
          src="https://cdn.pixabay.com/photo/2017/05/23/22/33/birthday-2338813_1280.jpg"
          alt="Introduction"
          style={{ height: '500px', width: '100%', borderRadius: '10px', objectFit: 'cover', filter: 'brightness(0.7)' }}
        />
        <div style={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', textAlign: 'center' }}>
          <h2 style={{ color: '#fff', fontSize: '48px', marginBottom: '20px' }}>Welcome to Our Premium Bakery</h2>
          <button
            className="secondary-button"
            style={{
              padding: '15px 30px',
              background: '#FFA500',
              color: '#fff',
              borderRadius: '25px',
              cursor: 'pointer',
              border: 'none',
              fontSize: '18px',
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.3)'
            }}
          >
            Get Started <FiArrowRight />
          </button>
        </div>
      </div>

      <form className="d-flex me-2" onSubmit={handleSearchUsers} style={{ justifyContent: 'center', marginBottom: '40px' }}>
        <input
          className="form-control me-2"
          type="text"
          placeholder="Search Product..."
          aria-label="Search"
          value={searchQueryUsers}
          onChange={(e) => setSearchQueryUsers(e.target.value)}
          style={{
            width: '300px',
            padding: '10px',
            marginRight: '10px',
            borderRadius: '25px',
            border: '1px solid #ccc'
          }}
        />
        <button className="btn btn-outline-success" type="submit" style={{
          padding: '10px 20px',
          borderRadius: '25px',
          border: '1px solid #98FB98',
          backgroundColor: '#98FB98',
          color: '#fff',
          cursor: 'pointer'
        }}>Search</button>
      </form>

      <h2 style={{ textAlign: 'center', color: '#FF69B4', fontWeight: 'bold', marginBottom: '20px' }}>New Arrivals</h2>

      <div style={{ display: 'flex', justifyContent: 'space-around', flexWrap: 'wrap', marginBottom: '40px' }}>
        {products.map((product, index) => (
          <div key={product._id} style={{
            margin: '20px',
            padding: '20px',
            border: '1px solid #ddd',
            borderRadius: '15px',
            textAlign: 'center',
            width: '250px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
            backgroundColor: '#fff',
            position: 'relative'
          }}>
            {product.isPremium && <span style={{
              position: 'absolute',
              top: '10px',
              right: '10px',
              backgroundColor: '#FFD700',
              color: '#fff',
              padding: '5px 10px',
              borderRadius: '5px',
              fontSize: '12px',
              fontWeight: 'bold'
            }}>Premium</span>}
            <img src={product.productImageUrl} alt={product.productName} style={{
              width: '100%',
              height: '200px',
              objectFit: 'cover',
              borderRadius: '10px'
            }} />
            <h3 style={{ marginTop: '10px', color: '#333' }}>{product.productName}</h3>
            <h3 style={{ marginTop: '10px', color: '#333' }}>NPR {product.productPrice}</h3>
            <button onClick={() => addToCart(index)} style={{
              padding: '10px',
              background: '#FF69B4',
              color: '#fff',
              borderRadius: '5px',
              cursor: 'pointer',
              border: 'none',
              marginTop: '10px'
            }}>
              Add to Cart
            </button>
            <button onClick={() => addToFavorites(index)} style={{
              padding: '10px',
              background: '#FFA500',
              color: '#fff',
              borderRadius: '5px',
              cursor: 'pointer',
              border: 'none',
              marginTop: '10px',
              marginLeft: '10px'
            }}>
              Add to Favorites
            </button>
          </div>
        ))}
      </div>

      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', marginTop: '40px', gap: '20px', marginBottom: '40px' }}>
        <div style={{
          textAlign: 'left',
          padding: '20px',
          backgroundColor: '#FF69B4',
          borderRadius: '15px',
          color: '#fff'
        }}>
          <h3 style={{ fontWeight: 'bold' }}>Our Services</h3>
          <ul>
            <li>Buy Bakery product</li>
            <li>Bakery</li>
            <li>Bakeryshop</li>
          </ul>
        </div>
